package uk.gov.moj.cpp.progression.service.disqualificationreferral;import static org.hamcrest.CoreMatchers.equalTo;import static org.hamcrest.MatcherAssert.assertThat;import uk.gov.justice.core.courts.CourtCentre;import uk.gov.justice.core.courts.LjaDetails;import org.junit.jupiter.api.Test;public class ReferralDisqualifyWarningEnglishDataAggregatorTest {    private final ReferralDisqualifyWarningEnglishDataAggregator referralDisqualifyWarningEnglishDataAggregator = new ReferralDisqualifyWarningEnglishDataAggregator();    @Test    public void testGetCourtHouseName() {        final String expectedCourtCentre = "Court Center";        final CourtCentre organisationUnit = CourtCentre.courtCentre().withName("Court Center").build();        final String actualCourtCentre = referralDisqualifyWarningEnglishDataAggregator.getCourtHouseName(organisationUnit);        assertThat(actualCourtCentre, equalTo(expectedCourtCentre));    }    @Test    public void testGetLjaName() {        final String ljaName = "lja Name";        final LjaDetails ljaDetails = LjaDetails.ljaDetails().withLjaCode("lja code").withLjaName("lja Name").withWelshLjaName("welsh name").build();        final String actualLja = referralDisqualifyWarningEnglishDataAggregator.getLjaName(ljaDetails);        assertThat(actualLja, equalTo(ljaName));    }}