package uk.gov.moj.cpp.progression.service.disqualificationreferral;import static org.hamcrest.CoreMatchers.equalTo;import static org.hamcrest.MatcherAssert.assertThat;import uk.gov.justice.core.courts.CourtCentre;import uk.gov.justice.core.courts.LjaDetails;import org.junit.jupiter.api.Test;public class ReferralDisqualifyWarningWelshDataAggregatorTest {    private final ReferralDisqualifyWarningWelshDataAggregator referralDisqualifyWarningWelshDataAggregator = new ReferralDisqualifyWarningWelshDataAggregator();    @Test    public void testGetCourtHouseName() {        final String expectedCourtCentre = "Welsh Court Center";        final CourtCentre organisationUnit = CourtCentre.courtCentre().withWelshName("Welsh Court Center").build();        final String actualCourtCentre = referralDisqualifyWarningWelshDataAggregator.getCourtHouseName(organisationUnit);        assertThat(actualCourtCentre, equalTo(expectedCourtCentre));    }    @Test    public void testGetLjaName() {        final String ljaName = "welsh name";        final LjaDetails ljaDetails = LjaDetails.ljaDetails().withLjaCode("lja code").withWelshLjaName("welsh name").build();        final String actualLja = referralDisqualifyWarningWelshDataAggregator.getLjaName(ljaDetails);        assertThat(actualLja, equalTo(ljaName));    }}