package uk.gov.moj.cpp.progression.helper;import static uk.gov.justice.services.integrationtest.utils.jms.JmsMessageConsumerClientProvider.newPrivateJmsMessageConsumerClientProvider;import static uk.gov.justice.services.integrationtest.utils.jms.JmsMessageConsumerClientProvider.newPublicJmsMessageConsumerClientProvider;import static uk.gov.moj.cpp.progression.helper.QueueUtil.retrieveMessage;import static uk.gov.moj.cpp.progression.it.framework.ContextNameProvider.CONTEXT_NAME;import uk.gov.justice.services.integrationtest.utils.jms.JmsMessageConsumerClient;import uk.gov.justice.services.messaging.JsonEnvelope;public class CpsServeHelper extends AbstractTestHelper {    private static final JmsMessageConsumerClient privateEventsCotrCreatedConsumer = newPrivateJmsMessageConsumerClientProvider(CONTEXT_NAME).withEventNames("progression.event.cotr-created").getMessageConsumerClient();    private static final JmsMessageConsumerClient privateEventsCotrServedConsumer = newPrivateJmsMessageConsumerClientProvider(CONTEXT_NAME).withEventNames("progression.event.prosecution-cotr-served").getMessageConsumerClient();    private static final JmsMessageConsumerClient privateEventsCotrUpdatedConsumer = newPrivateJmsMessageConsumerClientProvider(CONTEXT_NAME).withEventNames("progression.event.prosecution-cotr-updated").getMessageConsumerClient();    private static final JmsMessageConsumerClient publicEventsCotrCreatedConsumer = newPublicJmsMessageConsumerClientProvider().withEventNames("public.progression.cotr-created").getMessageConsumerClient();    private static final JmsMessageConsumerClient publicEventsCotrServedConsumer = newPublicJmsMessageConsumerClientProvider().withEventNames("public.progression.prosecution-cotr-served").getMessageConsumerClient();    private static final JmsMessageConsumerClient publicEventsCotrFailedConsumer = newPublicJmsMessageConsumerClientProvider().withEventNames("public.progression.cotr-operation-failed").getMessageConsumerClient();    private static final JmsMessageConsumerClient publicEventsCotrUpdatedConsumer = newPublicJmsMessageConsumerClientProvider().withEventNames("public.progression.cotr-updated").getMessageConsumerClient();    public JsonEnvelope getPrivateEventsCotrCreatedConsumer() {        return retrieveMessage(privateEventsCotrCreatedConsumer).orElse(null);    }    public JsonEnvelope getPublicEventsCotrCreatedConsumer() {        return retrieveMessage(publicEventsCotrCreatedConsumer).orElse(null);    }    public JsonEnvelope getPrivateEventsCotrServedConsumer() {        return retrieveMessage(privateEventsCotrServedConsumer).orElse(null);    }    public JsonEnvelope getPublicEventsCotrServedConsumer() {        return retrieveMessage(publicEventsCotrServedConsumer).orElse(null);    }    public JsonEnvelope getPrivateEventsCotrUpdatedConsumer() {        return retrieveMessage(privateEventsCotrUpdatedConsumer).orElse(null);    }    public JsonEnvelope getPublicEventsCotrUpdatedConsumer() {        return retrieveMessage(publicEventsCotrUpdatedConsumer).orElse(null);    }    public JsonEnvelope getPublicEventsCotrFailedConsumer() {        return retrieveMessage(publicEventsCotrFailedConsumer).orElse(null);    }}