package uk.gov.moj.cpp.progression.helper;import static uk.gov.moj.cpp.progression.helper.QueueUtil.retrieveMessageAsEnvelope;import uk.gov.justice.services.messaging.JsonEnvelope;public class CpsServeMaterialHelper extends AbstractTestHelper {    public CpsServeMaterialHelper() {        this.privateEventsConsumer = QueueUtil.privateEvents.createPrivateConsumerForMultipleSelectors(                "progression.event.pet-form-created","progression.event.form-created");        this.publicEventsConsumer = QueueUtil.publicEvents.createPublicConsumerForMultipleSelectors(                "public.progression.pet-form-created","public.progression.form-created");    }    public JsonEnvelope getPrivateEvents() {        return retrieveMessageAsEnvelope(privateEventsConsumer);    }    public JsonEnvelope getPublicEvents() {        return retrieveMessageAsEnvelope(publicEventsConsumer);    }}